import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import axios from "axios";

export default class App extends Component {
  constructor(props) {
      super(props);
      this.state = {
          data1 : [],
          sort : True
      };

      this.All = this.All.bind(this)
      this.All()    

  }

All() {
  axios({
    method: "GET",
    url:"http://127.0.0.1:5000/",
  })
  .then((response) => {
    this.setState({'data1' : response.data})
    console.log(response)
    console.log("name",response.data[0].name)
  })}


render(){
  return ( 
    <div className='container '>
      <table className="table table-striped table-hover table-bordered" style={{ marginTop: "100px", width: "1400px" }}>          
      <thead>
        <tr>
          <td>S.No </td>
          <td >Name <i className="fa fa-fw fa-sort"></i> </td>
          <td>Email </td>           
        </tr>
    </thead>
    <tbody>{this.state.data1.map((x,index) => 
    <tr key={index}>
    <td>{index+1}</td>
    <td >{x.name}</td>
    <td>{x.email}</td>                  
    </tr>
         )}
  </tbody>
  </table>
    </div>    
  )
}
}
