import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import axios from "axios";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data1: [],
      URL: "http://127.0.0.1:5000/",
      sort_item: "asc"
    };

    this.sorting_item = this.sorting_item.bind(this)
  }

  componentWillMount() {
    console.log("Again1 ")
    axios({
      method: "GET",
      url: this.state.URL,
    })
      .then((response) => {
        this.setState({ 'data1': response.data })
      })
  }
  sorting_item(e) {
    console.log("sort")
    if (this.state.sort_item == 'asc') {
      this.setState({ URL: "http://127.0.0.1:5000/?sort=asc", sort_item: 'dsc' });
    }
    if (this.state.sort_item == 'dsc') {
      this.setState({ URL: "http://127.0.0.1:5000/?sort=dsc", sort_item: 'asc' });
    }
  }

  filtered_item(e){
    //this.setState({URL : "http://127.0.0.1:5000/?sort=dsc"+ Document.getElementById('search1')})
    console.log("http://127.0.0.1:5000/?sort=dsc"+ (document.getElementById('form1')))
   }

  render() {
    return (
      <div>

          <div className='row'>
          <div class="input-group" style={{marginLeft: "10px"}}>
            <div id="search-autocomplete" class="col-sm-2 offset-sm-6 mt-5 mb-4 form-outline">
              <input type="search" id="form1" class="form-control" />
            </div>
            <button type="button" class="btn btn-primary" style={{marginTop : "48px",marginBottom : "57px"}}>
              <i onClick={e => this.filtered_item(e)} class="fa fa-fw fa-search"></i>
            </button>
          </div>

        </div>
        <div className='container'>
          <table className="table table-striped table-hover table-bordered" style={{ marginTop: "90px", width: "1400px" }}>
            <thead>
              <tr>
                <td>S.No </td>
                <td >Name <i onClick={e => this.sorting_item(e)} className="fa fa-fw fa-sort"></i> </td>
                <td>Email </td>
              </tr>
            </thead>
            <tbody>{this.state.data1.map((x, index) =>
              <tr key={index}>
                <td>{index + 1}</td>
                <td >{x.name}</td>
                <td>{x.email}</td>
              </tr>
            )}
            </tbody>
          </table>
        </div>
      </div>

    )
  }
}
