import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import axios from "axios";

export default class App extends Component {
  constructor(props) {
      super(props);
      this.state = {
          data1 : []

      };      
      this.All = this.All.bind(this)
      this.All()          
  }

All() {
  axios({
    method: "GET",
    url:"http://127.0.0.1:5000/",
  })
  .then((response) => {
    this.setState({'data1' : response.data})
  })}

render(){
  return ( 
    <div>
    <div className = 'row'>
    <div class="col-sm-1 offset-sm-1 mt-5 mb-4 text-gred">
    <div className="search">
      <form className="form-inline">
       <input className="form-control mr-sm-2" type="search" placeholder="Search.." aria-label="Search"/><button className="btn btn-primary" ><i className="fa fa-fw fa-search"></i></button>      
      </form>
    </div>    
    </div>  
    <div className="col-sm-3 offset-sm-6  mt-5 mb-4 text-gred">
    <button className="btn btn-primary" > Add New Profile </button>
   </div>
 </div>
    <div className='container'>
      <table className="table table-striped table-hover table-bordered" style={{ marginTop: "90px", width: "1400px" }}>          
      <thead>
        <tr>
          <td>S.No </td>
          <td >Name <i className="fa fa-fw fa-sort"></i> </td>
          <td>Email </td>           
        </tr>
    </thead>
    <tbody>{this.state.data1.map((x,index) => 
    <tr key={index}>
    <td>{index+1}</td>
    <td >{x.name}</td>
    <td>{x.email}</td>                  
    </tr>
         )}
  </tbody>
  </table>
    </div>  
    </div>  

  )
}
}
