      <table className="table table-striped table-hover table-bordered" style={{ marginTop: "100px", width: "1400px" }}>          
      <thead>
        <tr>
          <td>S.No </td>
          <td >Name <i className="fa fa-fw fa-sort"></i> </td>
          <td>Email </td>           
        </tr>
    </thead>
    <tbody>{this.state.data1.map((x,index) => 
    <tr key={index}>
    <td>{index+1}</td>
    <td >{x.name}</td>
    <td>{x.email}</td>                  
    </tr>
         )}
  </tbody>
  </table>
    </div>

  )
}

}
