  //       this.setState({ URL: "http://127.0.0.1:5000/?sort=dsc", sort_item: 'asc' });
  //       this.api_call()
  //     }
  //   }
  //   filtered_item(item){  
  //     if (this.state.sort_item == 'asc') {
  //       this.setState({ URL: "http://127.0.0.1:5000/?sort=asc"+'&filter='+item });
  //       this.api_call()
  //     }
  //     if (this.state.sort_item == 'dsc') {
  //       this.setState({ URL: "http://127.0.0.1:5000/?sort=dsc"+'&filter='+item });
  //       this.api_call()
  //     }
  //    }

  render() {
    return (
      <div>
        <div className='row'>
          <div className="input-group" style={{ Left: "10px" }}>
            <div id="search-autocomplete" className="col-sm-2 offset-sm-9 mt-5 mb-4 form-outline">
              <input type="search" id="form1" className="form-control" />
            </div>
            <button type="button" className="btn btn-primary" style={{ marginTop: "48px", marginBottom: "57px" }}>
              <i onClick={e => this.filtered_item(document.getElementById('form1').value.toLowerCase())} className="fa fa-fw fa-search"></i>
            </button>
          </div>

        </div>
        <div className='container'>
          <table className="table table-striped table-hover table-bordered" style={{ marginTop: "60px", width: "1400px" }}>
            <thead>
              <tr>
                <td>S.No </td>
                <td >Name <i onClick={e => this.sorting_item(e)} className="fa fa-fw fa-sort"></i> </td>
                <td>Email </td>
              </tr>
            </thead>
            <tbody>{this.state.data1.map((x, index) =>
              <tr key={index}>
                <td>{index + 1}</td>
                <td >{x.name}</td>
                <td>{x.email}</td>
              </tr>
            )}
            </tbody>
          </table>
        </div>


        <div><button type="button" style={{ position: "fixed", top: " 800px", float: "right" }} className="btn btn-lg btn-success" data-toggle="modal" data-target="#exampleModalCenter">ADD</button></div>



        {this.state.Lib.length ?

          <div className="modal fade" id="exampleModalCenter" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
            <div className="modal-dialog modal-dialog-centered" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLongTitle">Add Data to API...</h5>
                  <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <div className="card-body" >
                    <div className="container d-flex align-items-center justify-content-center">

                      <form >
                        <div className="form-outline col-12">
                          <label className="form-label" >Employee ID</label>
                          <input type="number" id="form4Example1" className="form-control" />
                        </div>
                        <div className="form-outline col-12">
                          <label className="form-label" >Name</label>
                          <input type="text" id="form4Example2" className="form-control" />
                        </div>
                        <div className="form-outline col-12">
                          <label className="form-label" >Project</label>
                          <input type="text" id="form4Example3" className="form-control" />
                        </div>
                        <div className="form-outline col-12">
                          <label className="form-label" >Position</label>
                          <input type="text" id="form4Example4" className="form-control" />
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                  <button type="button" onClick={(e) => this.fun_1(e)} className="btn btn-primary">Add</button>
                </div>
              </div>
            </div>
          </div>
          : null}
      </div>

    )
  }
}
